'use strict';

/**
* A list of French stop words.
*
* @module @stdlib/datasets/savoy-stopwords-fr
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-fr' );
* var list = stopwords();
* // returns [ 'a', 'à', 'â', 'abord', 'afin', 'ah', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_fr.js' );
}


// EXPORTS //

module.exports = stopwords;
