'use strict';

// MODULES //

var data = require( './../data/words.json' );


// FRENCH STOPWORDS //

/**
* Returns a list of French stop words.
*
* @returns {StringArray} stop words
*
* @example
* var list = stopwords();
* // returns [ 'a', 'à', 'â', 'abord', 'afin', 'ah', ... ]
*/
function stopwords() {
	return data.slice();
} // end FUNCTION stopwords()


// EXPORTS //

module.exports = stopwords;
