'use strict';

/**
* A list of German stop words.
*
* @module @stdlib/datasets/savoy-stopwords-ger
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-ger' );
* var list = stopwords();
* // returns [ 'a', 'ab', 'aber', 'ach', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_ger.js' );
}


// EXPORTS //

module.exports = stopwords;
