'use strict';

/**
* A list of Italian stop words.
*
* @module @stdlib/datasets/savoy-stopwords-it
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-it' );
* var list = stopwords();
* // returns [ 'a', 'abbastanza', 'accidenti', 'ad', 'adesso', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_it.js' );
}


// EXPORTS //

module.exports = stopwords;
