'use strict';

/**
* A list of Portuguese stop words.
*
* @module @stdlib/datasets/savoy-stopwords-por
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-por' );
* var list = stopwords();
* // returns [ 'a', 'à', 'adeus', 'agora', 'aí', 'ainda', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_por.js' );
}


// EXPORTS //

module.exports = stopwords;
