'use strict';

// MODULES //

var data = require( './../data/words.json' );


// MAIN //

/**
* Returns a list of Portuguese stop words.
*
* @returns {StringArray} stop words
*
* @example
* var list = stopwords();
* // returns [ 'a', 'à', 'adeus', 'agora', 'aí', 'ainda', ... ]
*/
function stopwords() {
	return data.slice();
} // end FUNCTION stopwords()


// EXPORTS //

module.exports = stopwords;
