'use strict';

// MODULES //

var data = require( './../data/words.json' );


// MAIN //

/**
* Returns a list of Swedish stop words.
*
* @returns {StringArray} stop words
*
* @example
* var list = stopwords();
* // returns [ 'aderton', 'adertonde', 'adj√∂', 'aldrig', 'alla', 'allas', ... ]
*/
function stopwords() {
	return data.slice();
}


// EXPORTS //

module.exports = stopwords;
