'use strict';

/**
* A list of Swedish stop words.
*
* @module @stdlib/datasets/savoy-stopwords-swe
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-swe' );
* var list = stopwords();
* // returns [ 'aderton', 'adertonde', 'adj√∂', 'aldrig', 'alla', 'allas', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_swe.js' );
}


// EXPORTS //

module.exports = stopwords;
