'use strict';

var sotu = require( './../lib' );
var speeches;

// Get a range of speeches:
speeches = sotu({
	'range': [ 2005, 2008 ]
});
console.dir( speeches );
// => [...]

// Get speeches by one or more Presidents:
speeches = sotu({
	'name': [
		'Ronald Reagan',
		'William J. Clinton'
	]
});
console.log( 'Number of addresses by Ronald Reagan and Bill Clinton: %d', speeches.length );

// Get all addresses by Presidents belonging to the Democratic Party:
speeches = sotu({
	'party': 'Democratic'
});
console.log( 'Number of addresses by Democrats: %d', speeches.length );
