'use strict';

// VARIABLES //

var revPartyDict;
var partyDict;
var key;


// PARTY ABBREVIATION DICTIONARY //

partyDict = {
	'd': 'Democratic',
	'dr': 'Democratic-Republican',
	'f': 'Federalist',
	'r': 'Republican',
	'nu': 'National Union',
	'n': 'None',
	'w': 'Whig',
	'wd': 'Whig & Democratic'
};
revPartyDict = {};

// TODO: Replace with eslint
/* jshint -W089 */
for ( key in partyDict ) {
	revPartyDict[ partyDict[key] ] = key;
}


// EXPORTS //

module.exports = {
	'abbreviation': revPartyDict,
	'fullName': partyDict
};
