'use strict';

// MODULES //

var fs = require( 'fs' );
var join = require( 'path' ).join;
var readDir = require( '@stdlib/fs/read-dir' ).sync;
var extname = require( '@stdlib/utils/extname' );
var dataDir = require( './data_dir.js' );
var RE = require( './re_filename.js' );


// LIST //

/**
* Generates a sorted list of JSON data file filenames.
*
* @private
* @returns {StringArray} filenames
*/
function list() {
	var fpath;
	var files;
	var stats;
	var out;
	var i;
	files = readDir( dataDir );
	out = [];
	for ( i = 0; i < files.length; i++ ) {
		fpath = join( dataDir, files[ i ] );
		stats = fs.statSync( fpath );
		if (
			stats.isFile() &&
			extname( files[ i ] ) === '.json' &&
			RE.test( files[ i ] )
		) {
			out.push( files[ i ] );
		}
	}
	return out.sort();
} // end FUNCTION list()


// EXPORTS //

module.exports = list();
