'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/index-of' );
var RE = require( './re_filename.js' );
var unsnakecase = require( './unsnakecase.js' );


// HAS NAME //

/**
* Checks if the name in a filename matches a name in a provided list.
*
* @private
* @param {string} filename - file name
* @param {StringArray} names - names to match
* @returns {boolean} boolean indicating if a filename has a matching name
*/
function hasName( filename, names ) {
	// Extract the name from the filename:
	var name = filename.match( RE )[ 2 ];

	// Convert the name from snakecase:
	name = unsnakecase( name );

	// Determine if we have a match:
	return ( indexOf( names, name ) !== -1 );
} // end FUNCTION hasName()


// EXPORTS //

module.exports = hasName;
