'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/index-of' );
var RE = require( './re_filename.js' );
var PARTY = require( './party.json' );


// HAS PARTY //

/**
* Checks if the party in a filename matches a party in a provided list.
*
* @private
* @param {string} filename - file name
* @param {StringArray} parties - parties to match
* @returns {boolean} boolean indicating if a filename has a matching party
*/
function hasParty( filename, parties ) {
	var party;

	// Extract the party abbreviation from the filename:
	party = filename.match( RE )[ 3 ];

	// Look-up the full party name:
	party = PARTY[ party ];

	// Determine if we have a match:
	return ( indexOf( parties, party ) !== -1 );
} // end FUNCTION hasParty()


// EXPORTS //

module.exports = hasParty;
