'use strict';

// MODULES //

var FILES = require( './file_list.js' );
var RE = require( './re_filename.js' );


// INDEX //

/**
* Generates an index for querying the SOTU database by year.
*
* @private
* @returns {Object} database index
*/
function index() {
	var out;
	var id;
	var i;
	out = {};
	for ( i = 0; i < FILES.length; i++ ) {
		// Extract a year from the filename:
		id = FILES[ i ].match( RE )[ 1 ];

		// Convert to an integer and subtract `1790` to zero-base the index:
		id = parseInt( id, 10 ) - 1790;

		// Adjust index due to two SOTU addresses being given in 1961 and 1953 and no address being given in 1933...
		if ( id > 1961 ) {
			id += 1;
		} else if ( id > 1933 && id <= 1953 ) {
			id -= 1;
		}
		if ( out.hasOwnProperty( id ) ) {
			out[ id ].push( i );
		} else {
			out[ id ] = [ i ];
		}
	}
	return out;
} // end FUNCTION index()


// EXPORTS //

module.exports = index();
