'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var removePunctuation = require( '@stdlib/string/remove-punctuation' );
var validate = require( './validate.js' );
var INDEX_YEAR = require( './index_year.js' );
var INDEX_NAME = require( './index_name.js' );
var INDEX_PARTY = require( './index_party.js' );
var FILES = require( './file_list.js' );
var intersect = require( './intersect.js' );
var getFile = require( './get_file.js' );


// SOTU ADDRESSES //

/**
* Returns State of the Union addresses.
*
* @param {Options} [options] - options object
* @param {(string|StringArray)} [options.name] - name of president(s)
* @param {PositiveIntegerArray} [options.range] - year range
* @param {(string|StringArray)} [options.party] - political party
* @returns {ObjectArray} SOTU addresses
*
* @example
* var speeches = sotu();
* // returns <ObjectArray>
*
* @example
* var speeches = sotu({
*	'president': [ 'Ronald Reagan', 'William J. Clinton' ]
* });
* // returns <ObjectArray>
*/
function sotu( options ) {
	var indices;
	var opts;
	var ids;
	var err;
	var out;
	var i;

	opts = {};
	if ( arguments.length > 0 ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	// Normalize options...
	if ( isString( opts.name ) ) {
		opts.name = [ opts.name ];
	}
	if ( opts.name ) {
		for ( i = 0; i < opts.name.length; i++ ) {
			opts.name[ i ] = removePunctuation( opts.name[ i ] );
		}
	}
	if ( isString( opts.party ) ) {
		opts.party = [ opts.party ];
	}
	if ( isNumber( opts.year ) ) {
		opts.year = [ opts.year ];
	}
	// Compute indices...
	indices = [];
	if ( opts.name ) {
		for ( i = 0; i < opts.name.length; i++ ) {
			if ( !INDEX_NAME.hasOwnProperty( opts.name[ i ] ) ) {
				throw new RangeError( 'invalid option. Unrecognized `name`. Option: `['+opts.name.join(',')+']`.' );
			}
			ids = INDEX_NAME[ opts.name[ i ] ];
			indices.push( ids );
		}
	}
	if ( opts.year ) {
		for ( i = 0; i < opts.year.length; i++ ) {
			if ( !INDEX_YEAR.hasOwnProperty( opts.year[ i ] ) ) {
				throw new RangeError( 'invalid option. Unrecognized `year`. Option: `['+opts.year.join(',')+']`.' );
			}
			ids = INDEX_YEAR[ opts.year[ i ] ];
			indices.push( ids );
		}
	}
	if ( opts.party ) {
		for ( i = 0; i < opts.party.length; i++ ) {
			if ( !INDEX_PARTY.hasOwnProperty( opts.party[ i ] ) ) {
				throw new RangeError( 'invalid option. Unrecognized `party`. Option: `['+opts.party.join(',')+']`.' );
			}
			ids = INDEX_PARTY[ opts.party[ i ] ];
			indices.push( ids );
		}
	}
	if ( opts.range ) {
		for ( i = opts.range[ 0 ]; i <= opts.range[ 1 ]; i++ ) {
			ids = INDEX_YEAR[ i ];
			if ( ids ) {
				indices.push( ids );
			}
		}
	}
	if ( indices.length ) {
		indices = intersect.apply( null, indices );
	} else {
		// If no options have been provided to limit the query range, return the entire dataset...
		for ( i = 0; i < FILES.length; i++ ) {
			indices.push( i );
		}
	}
	// Retrieve the data from the database...
	out = new Array( indices.length );
	for ( i = 0; i < indices.length; i++ ) {
		out[ i ] = getFile( indices[i] );
	}
	return out;
} // end FUNCTION sotu()


// EXPORTS //

module.exports = sotu;
