'use strict';

// MODULES //

var tape = require( 'tape' );
var unsnakecase = require( './../lib/unsnakecase.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof unsnakecase, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function converts a snakecase name to a normal name', function test( t ) {
	var out = unsnakecase( 'william_j_clinton' );
	t.equal( out, 'William J Clinton', 'converts snakecase name' );
	t.end();
});
