'use strict';

/**
* Synchronously loads a JSON file.
*
* @private
* @param {string} fpath - file path
* @returns {Object} JSON
*/
function loadJSON( fpath ) {
	return require( fpath ); // eslint-disable-line stdlib/no-dynamic-require
} // end FUNCTION loadJSON()


// EXPORTS //

module.exports = loadJSON;
