'use strict';

// MODULES //

var tape = require( 'tape' );
var IS_BROWSER = require( '@stdlib/assert/is-browser' );
var isArray = require( '@stdlib/assert/is-array' );
var db = require( './../lib/db.js' );


// VARIABLES //

var opts = {
	'skip': IS_BROWSER
};


// TESTS //

tape( 'main export is an array', function test( t ) {
	t.ok( true, __filename );
	t.equal( isArray( db ), true, 'main export is an array' );
	t.end();
});

tape( 'as of 2016, the exported array has a length greater than or equal to 228', opts, function test( t ) {
	t.equal( db.length >= 228, true, 'has length greater than or equal to 228 (as of 2016)' );
	t.end();
});

tape( 'the exported array is initialized with empty elements (data is lazily loaded)', opts, function test( t ) {
	var i;
	for ( i = 0; i < db.length; i++ ) {
		t.equal( db[i], void 0, 'element '+i+' is empty' );
	}
	t.end();
});
