'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var copy = require( '@stdlib/utils/copy' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var FILES = require( './../lib/file_list.js' );
var BROWSER_LIST = require( './../lib/browser_file_list.json' );


// TESTS //

tape( 'main export is a string array', function test( t ) {
	t.ok( true, __filename );
	t.equal( isStringArray( FILES ), true, 'main export is a string array' );
	t.end();
});

tape( 'as of 2016, export has a length greater than or equal to 228', function test( t ) {
	t.equal( FILES.length >= 228, true, 'has length greater than or equal to 228 (as of 2016)' );
	t.end();
});

tape( 'the array is sorted', function test( t ) {
	var arr = copy( FILES );
	arr.sort();
	t.deepEqual( FILES, arr, 'array is sorted' );
	t.end();
});

tape( 'when in a non-browser environment, the implementation guards against directories having the data filename signature', function test( t ) {
	var files;
	var bool;

	bool = false;
	files = proxyquire( './../lib/file_list.js', {
		'@stdlib/assert/is-browser': false,
		'fs': {
			'statSync': statSync
		}
	});
	t.equal( files.length < FILES.length && files.length > 0, true, 'does not include directories' );
	t.end();

	function statSync() {
		bool = !bool;
		return {
			'isFile': isFile
		};
	}

	function isFile() {
		return bool;
	}
});

tape( 'when in a browser environment, the export does not attempt to read the file system and instead returns a virtual file list', function test( t ) {
	var files = proxyquire( './../lib/file_list.js', {
		'@stdlib/assert/is-browser': true
	});

	t.deepEqual( files, BROWSER_LIST, 'returns a virtual file list' );
	t.end();
});
