'use strict';

// MODULES //

var tape = require( 'tape' );
var isObject = require( '@stdlib/utils/is-object' ); // TODO: plain object
var isPositiveIntegerArray = require( '@stdlib/utils/is-positive-integer' ).isPositivePrimitiveIntegerArray;
var index = require( './../lib/index_name.js' );


// TESTS //

tape( 'main export is a object', function test( t ) {
	t.ok( true, __filename );
	t.equal( isObject( index ), true, 'main export is an object' );
	t.end();
});

tape( 'export maps President names to data file indices', function test( t ) {
	t.equal( isPositiveIntegerArray( index[ 'Barack Obama' ] ), true, 'returns an array of indices' );
	t.end();
});
