'use strict';

/**
* A list of simple American-English words (revised Spache).
*
* @module @stdlib/datasets/spache-revised
*
* @example
* var words = require( '@stdlib/datasets/spache-revised' );
*
* var data = words();
* // returns [ 'a', 'able', 'about', 'above', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var words;
if ( IS_BROWSER ) {
	words = require( './browser.js' );
} else {
	words = require( './spache_revised.js' );
}


// EXPORTS //

module.exports = words;
