'use strict';

// MODULES //

var data = require( './../data/words.json' );


// MAIN //

/**
* Returns a list of simple American-English words (revised Spache).
*
* @returns {StringArray} words
*
* @example
* var list = words();
* // returns [ 'a', 'able', 'about', 'above', ... ]
*/
function words() {
	return data.slice();
} // end FUNCTION words()


// EXPORTS //

module.exports = words;
