'use strict';

// MODULES //

var copy = require( '@stdlib/utils/copy' );
var data = require( './../data/data.js' );


// MAIN //

/**
* Returns the Spam Assassin public email corpus.
*
* @returns {ObjectArray} data
*
* @example
* var data = corpus();
* // returns [ {...}, {...}, ... ]
*/
function corpus() {
	return copy( data );
} // end FUNCTION corpus()


// EXPORTS //

module.exports = corpus;
