'use strict';

/**
* Spam Assassin public email corpus.
*
* @module @stdlib/datasets/spam-assassin
*
* @example
* var corpus = require( '@stdlib/datasets/spam-assassin' );
*
* var data = corpus();
* // returns [ {...}, {...}, ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var corpus;
if ( IS_BROWSER ) {
	corpus = require( './browser.js' );
} else {
	corpus = require( './spam_assassin.js' );
}


// EXPORTS //

module.exports = corpus;
