'use strict';

// MODULES //

var copy = require( '@stdlib/utils/copy' );


// MAIN //

/**
* Returns the Spam Assassin public email corpus.
*
* @returns {ObjectArray} data
*
* @example
* var data = corpus();
* // returns [ {...}, {...}, ... ]
*/
function corpus() {
	// Lazy load to improve start-up times (e.g., in a REPL) and take advantage of require's caching mechanism...
	return copy( require( './../data/data.js' ) );
} // end FUNCTION corpus()


// EXPORTS //

module.exports = corpus;
