'use strict';

// MODULES //

var join = require( 'path' ).join;
var resolve = require( 'path' ).resolve;
var readJSON = require( '@stdlib/fs/read-json' ).sync;
var replace = require( '@stdlib/string/replace' );
var fileList = require( './../data/file_list.json' );


// VARIABLES //

var dataPath = resolve( __dirname, '..', 'data' );
var opts = {
	'encoding': 'utf8'
};


// MAIN //

/**
* Returns the Spam Assassin public email corpus.
*
* ## Notes
*
* * This function synchronously reads data from disk for each invocation. Such behavior is intentional and so is the avoidance of `require`. We assume that invocations are infrequent, and we want to avoid the `require` cache. This means that we allow data to be garbage collected and a user is responsible for explicitly caching data.
*
*
* @throws {Error} unable to read data
* @returns {ObjectArray} data
*
* @example
* var data = corpus();
* // returns [ {...}, {...}, ... ]
*/
function corpus() {
	var fpath;
	var data;
	var text;
	var i;

	data = new Array( 6046 );
	for ( i = 0; i < data.length; i++ ) {
		fpath = join( dataPath, replace( fileList[ i ], 'txt', 'json' ) );
		text = readJSON( fpath, opts );
		if ( text instanceof Error ) {
			throw text;
		}
		data[ i ] = text;
	}
	return data;
} // end FUNCTION corpus()


// EXPORTS //

module.exports = corpus;
