/*
* This script renames raw Spam Assassin files of the form `<number>.<hash>` by appending the extension `.txt`.
*/
'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var rename = require( 'fs' ).renameSync;
var readDirSync = require( '@stdlib/fs/read-dir' ).sync;


// VARIABLES //

var RE_EXT = /\.txt$/;
var dirs = [
	'easy-ham',
	'easy-ham-2',
	'hard-ham',
	'spam',
	'spam-2'
];


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var fpath1;
	var fpath2;
	var dpath;
	var files;
	var i;
	var j;

	for ( i = 0; i < dirs.length; i++ ) {
		dpath = resolve( __dirname, '..', 'data', dirs[ i ] );
		files = readDirSync( dpath );
		if ( files instanceof Error ) {
			throw files;
		}
		for ( j = 0; j < files.length; j++ ) {
			if ( files[ j ][ 0 ] === '.' ) {
				// Skip "hidden" files...
				continue;
			}
			if ( !RE_EXT.test( files[ j ] ) ) {
				fpath1 = resolve( __dirname, '..', 'data', dirs[ i ], files[ j ] );
				fpath2 = fpath1 + '.txt';
				rename( fpath1, fpath2 );
			}
		}
	}
} // end FUNCTION main()

main();
