'use strict';

/**
* A list of English stopwords.
*
* @module @stdlib/datasets/stopwords-en
*
* @example
* var stopwords = require( '@stdlib/datasets/stopwords-en' );
* var list = stopwords();
* // returns [ 'a', 'about', 'above', 'across', 'actually', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './stopwords_en.js' );
}


// EXPORTS //

module.exports = stopwords;
