'use strict';

// MODULES //

var tape = require( 'tape' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isArray = require( '@stdlib/utils/is-array' );
var list = require( './../lib' );


// TESTS //

tape( 'main export is an array of strings', function test( t ) {
	var i;
	t.ok( true, __filename );
	t.equal( isArray( list ), true, 'main export is an array' );
	for ( i = 0; i < list.length; i++ ) {
		t.equal( isString( list[i] ), true, 'element '+i+' is a string' );
	}
	t.end();
});
