tape( 'the command-line interface prints a list of data sets', opts, function test( t ) {
	var expected;
	var cmd;

	cmd = [
		process.execPath,
		fpath,
		'--ls'
	];

	expected = objectKeys( NAMES ).join( '\n' );

	exec( cmd.join( ' ' ), done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			stdout = stdout.toString();
			expected = expected.toString();
			t.strictEqual( stdout, expected+'\n', 'prints list of data sets' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

tape( 'the command-line interface prints a chosen dataset', opts, function test( t ) {
	var expected;
	var cmd;

	cmd = [
		process.execPath,
		fpath,
		'--name MONTH_NAMES_EN'
	];

	expected = [
		'January',
		'February',
		'March',
		'April',
		'May',
		'June',
		'July',
		'August',
		'September',
		'October',
		'November',
		'December'
	].join( '\n' );

	exec( cmd.join( ' ' ), done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			stdout = stdout.toString();
			expected = expected.toString();
			t.strictEqual( stdout, expected+'\n', 'prints data set' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

tape( 'the command-line interface prints a chosen dataset (dataset-specific options)', opts, function test( t ) {
	var header;
	var cmd;

	cmd = [
		process.execPath,
		fpath,
		'--name MINARD_NAPOLEONS_MARCH -- --data army'
	];

	// Column names for the army data set:
	header = 'lon,lat,size,direction,division';

	exec( cmd.join( ' ' ), done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			stdout = stdout.toString();
			t.strictEqual( contains( stdout, header ), true, 'prints data set' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});
