'use strict';

/**
* A list of US state two-letter abbreviations in alphabetical order according to state name.
*
* @module @stdlib/datasets/us-states-abbr
*
* @example
* var abbr = require( '@stdlib/datasets/us-states-abbr' );
*
* var data = abbr();
* // returns [ 'AL', 'AK', 'AZ', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var abbr;
if ( IS_BROWSER ) {
	abbr = require( './browser.js' );
} else {
	abbr = require( './us_states_abbr.js' );
}


// EXPORTS //

module.exports = abbr;
