'use strict';

/**
* Maps US state capitals to state names.
*
* @module @stdlib/datasets/us-states-capitals-names
*
* @example
* var table = require( '@stdlib/datasets/us-states-capitals-names' );
* var tbl = table();
* // returns {...}
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var table;
if ( IS_BROWSER ) {
	table = require( './browser.js' );
} else {
	table = require( './us_states_capitals_names.js' );
}


// EXPORTS //

module.exports = table;
