'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var isObject = require( '@stdlib/assert/is-plain-object' );
var proxyquire = require( 'proxyquire' );
var table = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof table, 'function', 'main export is a function' );
	t.end();
});

tape( 'main export is a function (browser)', function test( t ) {
	var table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});
	t.equal( typeof table, 'function', 'main export is a function' );
	t.end();
});

tape( 'main export is a function (non-browser)', function test( t ) {
	var table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});
	t.equal( typeof table, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an object', function test( t ) {
	var tbl = table();
	t.equal( isObject( tbl ), true, 'returns an object' );
	t.end();
});

tape( 'the function returns an object (browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});

	tbl = table();
	t.equal( isObject( tbl ), true, 'returns an object' );
	t.end();
});

tape( 'the function returns an object (non-browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});

	tbl = table();
	t.equal( isObject( tbl ), true, 'returns an object' );
	t.end();
});

tape( 'the returned object has 50 keys', function test( t ) {
	var tbl = table();
	t.equal( getKeys( tbl ).length, 50, 'has 50 keys' );
	t.end();
});

tape( 'the returned object has 50 keys (browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});

	tbl = table();
	t.equal( getKeys( tbl ).length, 50, 'has 50 keys' );
	t.end();
});

tape( 'the returned object has 50 keys (non-browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});

	tbl = table();
	t.equal( getKeys( tbl ).length, 50, 'has 50 keys' );
	t.end();
});

tape( 'the returned object maps US state capitals to state names', function test( t ) {
	var tbl = table();
	t.equal( tbl[ 'Jefferson City' ], 'Missouri', 'returns Missouri' );
	t.equal( tbl[ 'Trenton' ], 'New Jersey', 'returns New Jersey' );
	t.equal( tbl[ 'Sacramento' ], 'California', 'returns California' );
	t.end();
});

tape( 'the returned object maps US state capitals to state names (browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});

	tbl = table();
	t.equal( tbl[ 'Jefferson City' ], 'Missouri', 'returns Missouri' );
	t.equal( tbl[ 'Trenton' ], 'New Jersey', 'returns New Jersey' );
	t.equal( tbl[ 'Sacramento' ], 'California', 'returns California' );
	t.end();
});

tape( 'the returned object maps US state capitals to state names (non-browser)', function test( t ) {
	var table;
	var tbl;

	table = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});

	tbl = table();
	t.equal( tbl[ 'Jefferson City' ], 'Missouri', 'returns Missouri' );
	t.equal( tbl[ 'Trenton' ], 'New Jersey', 'returns New Jersey' );
	t.equal( tbl[ 'Sacramento' ], 'California', 'returns California' );
	t.end();
});
