'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var isObject = require( '@stdlib/utils/is-object' ); // TODO: plain object
var table = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( isObject( table ), true, 'main export is an object' );
	t.end();
});

tape( 'export has 50 keys', function test( t ) {
	t.equal( getKeys( table ).length, 50, 'has 50 keys' );
	t.end();
});

tape( 'export maps US state capitals to state names', function test( t ) {
	t.equal( table[ 'Jefferson City' ], 'Missouri', 'returns Missouri' );
	t.equal( table[ 'Trenton' ], 'New Jersey', 'returns New Jersey' );
	t.equal( table[ 'Sacramento' ], 'California', 'returns California' );
	t.end();
});
