'use strict';

/**
* A list of US state capitals in alphabetical order according to state name.
*
* @module @stdlib/datasets/us-states-capitals
*
* @example
* var capitals = require( '@stdlib/datasets/us-states-capitals' );
*
* var data = capitals();
* // returns [ 'Montgomery', 'Juneau', 'Phoenix', 'Little Rock', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var capitals;
if ( IS_BROWSER ) {
	capitals = require( './browser.js' );
} else {
	capitals = require( './us_states_capitals.js' );
}


// EXPORTS //

module.exports = capitals;
