'use strict';

/**
* Maps US state names to state capitals.
*
* @module @stdlib/datasets/us-states-names-capitals
*
* @example
* var table = require( '@stdlib/datasets/us-states-names-capitals' );
* var tbl = table();
* // returns {...}
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var table;
if ( IS_BROWSER ) {
	table = require( './browser.js' );
} else {
	table = require( './us_states_names_capitals.js' );
}


// EXPORTS //

module.exports = table;
