'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var isObject = require( '@stdlib/assert/is-plain-object' );
var table = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof table, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an object', function test( t ) {
	var tbl = table();
	t.equal( isObject( tbl ), true, 'returns an object' );
	t.end();
});

tape( 'the returned object has 50 keys', function test( t ) {
	var tbl = table();
	t.equal( getKeys( tbl ).length, 50, 'has 50 keys' );
	t.end();
});

tape( 'the returned object maps US state names to state capitals', function test( t ) {
	var tbl = table();
	t.equal( tbl[ 'Missouri' ], 'Jefferson City', 'returns capital of Missouri' );
	t.equal( tbl[ 'New Jersey' ], 'Trenton', 'returns capital of New Jersey' );
	t.equal( tbl[ 'California' ], 'Sacramento', 'returns capital of California' );
	t.end();
});
