'use strict';

/**
* Return the maximum value.
*
* @module @stdlib/fastmath/special/max
*
* @example
* var max = require( '@stdlib/fastmath/special/max' );
*
* var v = max( 3.14, 4.2 );
* // returns 4.2
*
* v = max( 3.14, NaN );
* // returns NaN
*
* v = max( NaN, 3.14 );
* // returns 3.14
*
* v = max( -0.0, +0.0 );
* // returns +0.0
*
* v = max( +0.0, -0.0 );
* // returns -0.0
*/

// MODULES //

var max = require( './max.js' );


// EXPORTS //

module.exports = max;
