'use strict';

/**
* Evaluate the exponential function.
*
* @module @stdlib/fastmath/special/pow-int
*
* @example
* var pow = require( '@stdlib/fastmath/special/pow-int' );
*
* var v = pow( 2.0, 3 );
* // returns 8.0
*
* v = pow( 3.14, 0 );
* // returns 1.0
*
* v = pow( 2.0, -2 );
* // returns 0.25
*
* v = pow( 0.0, 0 );
* // returns 1.0
*
* v = pow( -3.14, 1 );
* // returns -3.14
*
* v = pow( NaN, 0 );
* // returns NaN
*/

// MODULES //

var pow = require( './pow.js' );


// EXPORTS //

module.exports = pow;
