'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var pkg = require( './../package.json' ).name;
var exists = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var i;

	i = 0;
	b.tic();

	return next();

	function next() {
		i += 1;
		if ( i <= b.iterations ) {
			return exists( __filename, onExists );
		}
		b.toc();
		b.pass( 'benchmark finished' );
		b.end();
	}

	function onExists( error, bool ) {
		if ( error ) {
			b.fail( error.message );
		}
		if ( !isBoolean( bool ) ) {
			b.fail( 'should have returned a boolean' );
		}
		next();
	}
});

bench( pkg+':sync', function benchmark( b ) {
	var bool;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		bool = exists.sync( __filename );
		if ( !isBoolean( bool ) ) {
			b.fail( 'should have returned a boolean' );
		}
	}
	b.toc();
	if ( isBoolean( bool ) ) {
		b.pass( 'benchmark finished' );
	} else {
		b.fail( 'should have returned a boolean' );
	}
	b.end();
});
