'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// NAMESPACE //

/**
* Top-level namespace.
*
* @namespace fs
*/
var fs = {};

/**
* @name exists
* @memberof fs
* @readonly
* @type {Function}
* @see {@link module:@stdlib/fs/exists}
*/
setReadOnly( fs, 'exists', require( '@stdlib/fs/exists' ) );

/**
* @name readDir
* @memberof fs
* @readonly
* @type {Function}
* @see {@link module:@stdlib/fs/read-dir}
*/
setReadOnly( fs, 'readDir', require( '@stdlib/fs/read-dir' ) );

/**
* @name readFile
* @memberof fs
* @readonly
* @type {Function}
* @see {@link module:@stdlib/fs/read-file}
*/
setReadOnly( fs, 'readFile', require( '@stdlib/fs/read-file' ) );

/**
* @name readFileList
* @memberof fs
* @readonly
* @type {Function}
* @see {@link module:@stdlib/fs/read-file-list}
*/
setReadOnly( fs, 'readFileList', require( '@stdlib/fs/read-file-list' ) );


// EXPORTS //

module.exports = fs;
