'use strict';

// MODULES //

var tape = require( 'tape' );
var fs = require( 'fs' );
var isBrowser = require( '@stdlib/utils/is-browser' );
var readDir = require( './../lib/async.js' );


// FIXME //

// Don't run tests in the browser...for now...
var opts = {
	'skip': isBrowser
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof readDir, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function reads a directory', opts, function test( t ) {
	var expected;

	expected = fs.readdirSync( __dirname );
	readDir( __dirname, onRead );

	function onRead( error, actual ) {
		if ( error ) {
			t.ok( false, error.message );
		}
		t.deepEqual( expected, actual, 'returns directory contents' );

		t.end();
	}
});

tape( 'if the function encounters an error, the function returns the error', opts, function test( t ) {
	readDir( 'beepboopbapbop', onRead );

	function onRead( error ) {
		t.equal( error instanceof Error, true, 'returns an error' );
		t.end();
	}
});
