'use strict';

// MODULES //

var getKeys = require( 'object-keys' ).shim();


// CLEAR PENDING //

/**
* Clears pending timeouts.
*
* @private
* @param {Object} pending - hash containing pending timeout ids
*/
function clearPending( pending ) {
	var keys;
	var i;
	keys = getKeys( pending );
	for ( i = 0; i < keys.length; i++ ) {
		clearTimeout( pending[ keys[i] ] );
	}
} // end FUNCTION clearPending()


// EXPORTS //

module.exports = clearPending;
