'use strict';

// MODULES //

var tape = require( 'tape' );
var readFileList = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof readFileList, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a function to read files synchronously', function test( t ) {
	t.equal( typeof readFileList.sync, 'function', 'attached method for synchronously reading file contents' );
	t.end();
});
