'use strict';

var join = require( 'path' ).join;
var hasWebAssemblySupport = require( '@stdlib/utils/detect-wasm-support' );
var readWASM = require( './../lib' );

var fpath = join( __dirname, 'fixtures', 'file.wasm' );
readWASM( fpath, onRead );

function onRead( error, wasm ) {
	var bool;
	if ( error ) {
		throw error;
	}
	bool = hasWebAssemblySupport();

	// If WebAssembly is supported, create a WebAssembly module instance...
	if ( bool ) {
		wasm = new WebAssembly.Module( wasm ); // eslint-disable-line no-undef
		wasm = new WebAssembly.Instance( wasm, {} ); // eslint-disable-line no-undef
		console.log( wasm.exports.stdlib_hypot( 5.0, 12.0 ) );
	} else {
		console.log( wasm );
	}
}
