'use strict';

// MODULES //

var fs = require( 'fs' );


// MAIN //

/**
* Synchronously renames a file.
*
* @param {(string|Buffer)} oldPath - old path
* @param {(string|Buffer)} newPath - new path
* @returns {(Error|null)} error object or null
*
* @example
* var err = renameSync( './beep/boop.txt', './beep/foo.txt' );
* if ( err instanceof Error ) {
*     throw err;
* }
*/
function renameSync( oldPath, newPath ) {
	try {
		fs.renameSync( oldPath, newPath ); // eslint-disable-line no-sync
	} catch ( err ) {
		return err;
	}
	return null;
} // end FUNCTION renameSync()


// EXPORTS //

module.exports = renameSync;
