'use strict';

// MODULES //

var tape = require( 'tape' );
var rename = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof rename, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously rename a file', function test( t ) {
	t.strictEqual( typeof rename.sync, 'function', 'has method' );
	t.end();
});
