'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var pkg = require( './../package.json' ).name;
var resolveParentPath = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var opts;
	var i;

	opts = {
		'dir': __dirname
	};

	i = 0;
	b.tic();

	return next();

	function next() {
		i += 1;
		if ( i <= b.iterations ) {
			return resolveParentPath( 'package.json', opts, done );
		}
		b.toc();
		b.pass( 'benchmark finished' );
		b.end();
	}

	function done( error, path ) {
		if ( error ) {
			b.fail( error.message );
		}
		if ( path === null ) {
			b.fail( 'should return a path' );
		}
		next();
	}
});

bench( pkg+':sync', function benchmark( b ) {
	var opts;
	var path;
	var i;

	opts = {
		'dir': __dirname
	};

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		path = resolveParentPath.sync( 'package.json', opts );
		if ( path === null ) {
			b.fail( 'should return a path' );
		}
	}
	b.toc();
	if ( path === null ) {
		b.fail( 'should return a path' );
	} else {
		b.pass( 'benchmark finished' );
	}
	b.end();
});
