'use strict';

// MODULES //

var tape = require( 'tape' );
var resolveParentPath = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof resolveParentPath, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a function to resolve a parent path synchronously', function test( t ) {
	t.equal( typeof resolveParentPath.sync, 'function', 'has `sync` method' );
	t.end();
});
