'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var fs = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof fs, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains utilities for interfacing with the filesystem', function test( t ) {
	var keys = getKeys( fs );
	t.equal( keys.length > 0, true, 'has keys' );
	t.end();
});
